        monsters.add(new Monster("Вампирское отродье (Vampire Spawn)", "", "Средний", "нежить", "нейтрально-злое", "15 (естественная броня)", "82 (11d8 + 33)", "6 клеток", "16", "16", "16", "11", "10", "12", "13", "языки которые он знал при жизни", "5", "UNDERDARK, URBAN", "нежить", "темнозрение 12 клеток", "Perception +3, Stealth +6", Arrays.asList(new Trait("Регенерация", Collections.singletonList("Вампир восстанавливает 10 очков здоровья в начале каждого своего хода, если у него остается хотя бы одно очко здоровья и он не в солнечном свете или в проточной воде. Если вампир получает урон излучением или урон от святой воды, регенерация не действует до начала следующего хода вампира."), null), new Trait("Паучье Лазание", Collections.singletonList("Вампир может карабкаться по труднопроходимой местности, в том числе и по потолку, без каких-либо проверок."), null), new Trait("Слабые стороны вампира", Arrays.asList("Вампир имеет следующие изъяны: ", "Запрет. Вампир не может войти в жилище без приглашения одного из жителей.", "Урон от проточной воды. Вампир получает 20 урона ядом если он заканчивает свой ход в проточной воде.", "Кол в сердце. Вампир уничтожен, если его сердце будет проткнуто колющем деревянным оружием, пока он небоеспособен в своем логове .", "Сверхчувствительность к солнечному свету. Вампир получает 20 урона излучением когда начинает свой ход в солнечном свете. Находясь на свету, он имеет помеху на броски атаки и проверки навыков."), null)), false, Arrays.asList(new Trait("Мультиатака", Collections.singletonList("Вампир проводит две атаки, только одна из которых может быть Укусом."), null), new Trait("Укус", Collections.singletonList("Рукопашное Оружие: +6 к атаке, досягаемость: 1 клетка, одно согласное существо, или существо захваченное вампиром, небоеспособное, или удерживаемое. Попадание: 6 (1d6 + 3) колющего урона плюс 7 (2d6) урона некротикой. Максимум очков здоровья цели уменьшается на количество полученного урона некротикой, а вампир восстанавливает очков здоровья в количестве этого урона. Уменьшение максимума ОЗ длится до длительного отдыха. Если эффект уменьшает максимум ОЗ цели до 0, цель умирает."), Collections.singletonList("|61|")), new Trait("Когти", Collections.singletonList("Рукопашное Оружие: +6 к атаке, досягаемость: 1 клетка, одно существо. Попадание: 8 (2d4 + 3) рубящего урона. Вместо нанесения урона, вампир может захватить цель (сложность освобождения 13)."), Collections.singletonList("|6|2d4+3")))));
        monsters.add(new Monster("Варгулья [Vargoulle]", "", "Крошечный", "исчадие", "хаотично-злой", "12", "13 (3к4 + 6)", "1 клетка, полёт 8 клеток", "6", "14", "14", "4", "7", "2", "8", "понимает языки Бездны, Инфернальный, и любые языки, которые знал до того как стать варгульей, но не может говорить", "1", null, "исчадие", "тёмное зрение 60 фт", null, null, false, Arrays.asList(new Trait("Укус", Collections.singletonList("Рукопашная атака оружием: +4 к попаданию, досягаемость 5 фт., одна цель. Попадание: 5 (1к6 + 2) колющего урона плюс 10 (3к6) урона ядом."), null), new Trait("Поцелуй", Collections.singletonList("Варгулья целует одного недееспособного гуманоида в 5 футах от себя. Цель должна успешно пройти спасбросок Харизмы Сл 12 или стать проклятой. Проклятая цель теряет 1 очко Харизмы каждый час, а ее голова принимает извращенные изменения. Проклятие не прогрессирует пока цель находится в области солнечного света или под действием заклинания дневной свет [daylight] и это время не учитывается. Когда Харизма проклятого существа становится равна 2, оно умирает и его голова отрывается от тела и становится новой варгульей. Если сотворить на цель снятие проклятья [remove curse], высшее восстановление [greater restoration] или похожее заклинание до того, как трансформация завершится, то проклятие снимется. В таком случае отменяются изменения цели, сделанные проклятием."), null), new Trait("Ошеломляющий Визг", Collections.singletonList("Варгулья визжит. Каждый гуманоид и зверь не далее 30 футов от варгульи и способный ее слышать должен успешно пройти спасбросок Мудрости Сл 12 или стать испуганным до конца следующего хода варгульи. Испуганные таким образом цели ошеломлены. Если спасбросок цели успешен или эффект для нее закончен, то цель получает иммунитет к Ошеломляющему Визгу всех варгулий на 1 час."), null))));
        monsters.add(new Monster("Василиск (Basilisk)", "\n\nПутешественники иногда натыкаются в диких местах на объекты, которые выглядят как куски удивительно реалистичной резьбы по камню. Недостающие куски кажутся откушенными. Бывалые путешественники считают такие реликвии предупреждением о том, что где-то рядом находится василиск.\n\nАдаптирующийся хищник. Василискам комфортно в засушливом, умеренном или тропическом климате. Его логово располагается в пещере или другом защищённом месте. Однако чаще василисков встречают под землёй.\n\nВасилиски, родившиеся и выращенные в неволе, могут быть одомашнены и обучены. Обученный василиск знает, как избегать взгляда с теми, кого хозяин хочет защитить, что делает его опасным охранником. Именно из-за этого яйца василиска высоко ценятся. \n\nКаменный взгляд. Василиски слишком тяжеловесные для охоты, но им и не нужно преследовать добычу. Взгляда василиска может быть достаточно, чтобы быстро превратить жертву в пористый камень, а сильные челюсти василиска способны крошить камень. А уже в пищеводе камень снова принимает органическую форму.\n\nНекоторые алхимики утверждают, что знают, как обработать пищевод василиска и жидкости, находящиеся в нем. Из обработанного должным образом пищевода можно выделить масло, которое будет обращать окаменение. К сожалению, если жертва потеряла какую-либо часть своего тела, пока находилась в каменном виде, при оживлении она не восстановится. Если в каменном виде существо потеряло жизненно важный орган, например, голову, вернуть существо в первоначальное состояние при помощи этого масла будет невозможно.\n\n", "Средний", "монстр", "без мировоззрения", "12 (естественная броня)", "52 (8d8 + 16)", "4 клеток", "16", "8", "15", "2", "8", "7", "9", null, "3", "Горы", "монстр", "темнозрение 12 клеток", null, Collections.singletonList(new Trait("Окаменяющий взгляд", Arrays.asList("Если существо начинает свой ход в пределах 6 клеток от василиска и оба могут видеть друг друга, василиск может заставить существо сделать спасбросок со сложностью 12 по Телосложению, если василиск в сознании. В случае неудачи, существо начинает магически превращаться в камень и становится удерживаевым. Цель должна повторять спасбросок в конце каждого своего хода. В случае успеха эффект заканчивается. В случае неудачи существо становится камнем до тех пор пока состояние не будет снято заклинанием улучшенное восстановление [greater restoration] или другой магией способной сделать это.", "Если существо не застигнуто врасплох, оно может отвести глаза и избежать таким образом спасброска. Если существо отводит глаза, оно не может видеть василиска до начала своего следующего хода, затем оно может снова отвести глаза. Если в это время существо по какой-либо причине посмотрит на василиска, оно должно сделать спасбросок от Окаменения немедленно.", "Если василиск видит свое отражение в пределах 6 клеток от него при ярком свете, он ошибочно принимает себя за соперника и применяет Окаменяющий взгляд на себя."), null)), false, Collections.singletonList(new Trait("Укус", Collections.singletonList("Рукопашная атака оружием:: +5 к атаке, досягаемость: 1 клетка, одна цель. Попадание: 10(2d6+3) колющего урона плюс 7(2d6) урона ядом."), Collections.singletonList("Укус|5|2d6+3+2d6")))));
